t("#LIGHT_BLUE#You cannot prepare this trap: %s.", "#LIGHT_BLUE#Vous ne pouvez pas préparer ce piège: %s.", "logPlayer")
t("Not Prepared", "Non préparé", "_t")
t("Primed Trigger", "Déclenchement amorcé", "_t")
